require("dotenv").config();
const fetch = require('node-fetch');

async function testFormSubmission() {
    try {
        console.log("Testing form submission...");
        
        // Create test form data
        const formData = {
            name: "Test User",
            phone: "555-123-4567",
            email: "test@example.com",
            address: "123 Test St",
            city: "Test City",
            state: "TS",
            zip: "12345",
            vehicleDescription: "2020 Test Vehicle",
            vin: "1HGCM82633A123456",
            insuranceCompany: "Test Insurance",
            claimNumber: "CL123456",
            dateOfLoss: "2023-01-01",
            insuredName: "Test Insured",
            provider: "Test Provider",
            deductible: "500",
            repairPermission: true,
            additionalRepairs: true,
            payment: true,
            totalLoss: true,
            failureToPay: true,
            reviews: true,
            referralSources: {
                google: true,
                facebook: false,
                instagram: false
            },
            hasEstimate: true,
            hasReceivedCheck: false,
            signature: "data:image/png;base64,"
        };
        
        // Send the form data to the server
        const response = await fetch('http://localhost:8080/submit-form', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json',
            },
            body: JSON.stringify({
                formData,
                pdfType: 'dropoff',
                estimatorEmail: 'test@example.com'
            })
        });
        
        console.log('Response status:', response.status);
        
        const responseData = await response.json();
        console.log('Response data:', responseData);
        
        return responseData;
    } catch (error) {
        console.error("Form submission error:", error);
        return { success: false, error: error.message };
    }
}

testFormSubmission()
    .then(result => console.log("Test completed with result:", result))
    .catch(err => console.error("Test failed:", err));
